create table auth_resource
(
    id                 bigint unsigned auto_increment
        primary key,
    auth_resource_id   bigint(10)                         null,
    auth_resource_name varchar(100)                       not null,
    auth_path          varchar(255)                       null,
    auth_method        varchar(255)                       null,
    auth_description   varchar(255)                       null,
    auth_status        varchar(255)                       null,
    create_time        datetime default CURRENT_TIMESTAMP not null,
    update_time        datetime default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP,
    constraint auth_resource_resource_id_uindex
        unique (auth_resource_id),
    constraint auth_resource_resource_name_uindex
        unique (auth_resource_name)
);

