create table building
(
    id                   int auto_increment
        primary key,
    building_id          int                                null,
    campus_group_id      int                                null,
    building_name        varchar(255)                       null,
    total_floor          int                                null,
    in_gender            tinyint                            null,
    max_capacity         int                                null,
    current_capacity     int                                null,
    total_dormitory      int                                null,
    is_elevator          tinyint                            null,
    create_time          datetime default CURRENT_TIMESTAMP null,
    update_time          datetime default CURRENT_TIMESTAMP null on update CURRENT_TIMESTAMP,
    building_description varchar(255)                       null,
    constraint building_building_id_uindex
        unique (building_id)
);

